import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { Keyboard, Platform, StyleSheet, Text, View } from 'react-native';
import { KeyboardAvoidingView, TextInput, TouchableOpacity, Pressable } from 'react-native-web';
import Task from './components/Task';

export default function App() {

  const [border, setBorder] = useState(0);
  const priority = 'low';

  const [task, setTask] = useState();
  const [taskItems, setTaskItems] = useState([]);

  const setPriority = () => {
    priority = 'high';
    setBorder('3');
  }

  const handleAddTask = () => {
    if (task != null && task != "")
      setTaskItems([...taskItems, task])
  }

  const completeTask = (index) => {
    let itemsCopy = [...taskItems];
    itemsCopy.splice(index, 1);
    setTaskItems(itemsCopy);
  }


  return (
    <View style={styles.container}>
      <View style={styles.taskWrapper}>
        <Text style={styles.sectionTitle}>Today's Tasks</Text>

        <View style={styles.items}>
          {taskItems.map((item, index) => {
            return (
              <TouchableOpacity onPress={() => completeTask(index)} >
                <Task text={item} />
              </TouchableOpacity>
            )
          })}
        </View>
      </View>

      <KeyboardAvoidingView behavior={Platform.OS === "ios" ? "padding" : "height"}
        style={styles.writeTaskWrapper} >
        <TextInput style={styles.input} placeholder={"Write a Task Here"} value={task} onChangeText={text => setTask(text)} />
        <View style={styles.priorities} >
          <TouchableOpacity onPress={() => setPriority('high')} style={[styles.squareHigh, {borderWidth: border}]} />
        </View>
        <TouchableOpacity onPress={() => handleAddTask()}>
          <View style={styles.addWrapper}>
            <Text>+</Text>
          </View>
        </TouchableOpacity>
      </KeyboardAvoidingView>
    </View>
  );


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#E8EAED',
  },
  taskWrapper: {
    paddingTop: 80,
    paddingHorizontal: 20,

  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold',

  },
  items: {
    marginTop: 20,
  },
  writeTaskWrapper: {
    position: 'absolute',
    bottom: 60,
    width: '100%',
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',

  },
  input: {
    paddingVertical: 15,
    width: '75%',
    paddingHorizontal: 15,
    backgroundColor: '#FFF',
    borderRadius: 60,
    borderColor: '#C0C0C0',
    borderWidth: 1,
    marginLeft: 15,
  },
  addWrapper: {
    width: 50,
    height: 50,
    backgroundColor: '#FFF',
    borderRadius: 60,
    fontSize: 34,
    justifyContent: 'center',
    alignItems: 'center',
    borderColor: '#C0C0C0',
    borderWidth: 1,
    marginRight: 15,
  },
  picker: {
    width: 100,
    height: 50,
    backgroundColor: '#FFF',
    borderRadius: 60,
    borderColor: '#C0C0C0',
    borderWidth: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  priorities: {
    flex: 1,
    flexDirection: 'row',
    justifyContent: 'center',
  },
  squareHigh: {
    width: 24,
    height: 24,
    backgroundColor: '#f9b6eb',
    borderRadius: 5,
    marginRight: 10,
    borderColor: '#ba036b',
    borderWidth: 2,
},
squareMedium: {
  width: 24,
  height: 24,
  backgroundColor: '#BCF555',
  opacity: 0.4,
  borderRadius: 5,
  marginRight: 10,
},
squareLow: {
  width: 24,
  height: 24,
  backgroundColor: '#55BCF6',
  opacity: 0.4,
  borderRadius: 5,
},
})};
